import React from 'react';

const HeroSection = () => {
  return (
    <section 
      className="relative h-screen bg-cover bg-center flex items-center"
      style={{
        backgroundImage: "linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('https://images.unsplash.com/photo-1532938911079-1b06ac7ceec7?ixlib=rb-1.2.1&auto=format&fit=crop&w=1950&q=80')",
        minHeight: "500px"
      }}
    >
      <div className="container mx-auto px-4 text-center text-white z-10">
        <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6 leading-tight">
          Bringing Healthcare Closer to Underserved Communities
        </h1>
        <p className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto">
          Innovative, scalable, and sustainable solutions to improve access to healthcare.
        </p>
        <button className="bg-primary-DEFAULT hover:bg-primary-dark text-white font-bold py-3 px-8 rounded-lg shadow-lg transition duration-300 transform hover:scale-105">
          Explore Solutions
        </button>
      </div>
    </section>
  );
};

export default HeroSection;
